mapper.select:
select * from sys_menu
and ( title like concat('%',#{criteria.blurry},'%') or name like concat('%',#{criteria.blurry},'%') or permission like concat('%',#{criteria.blurry},'%') )
and pid is null
and pid = #{criteria.pid}
and create_time between #{criteria.createTime[0]} and #{criteria.createTime[1]}
order by menu_sort

SELECT m.* FROM sys_menu m, sys_roles_menus r
WHERE m.menu_id = r.menu_id AND r.role_id IN

    #{roleId}

AND type != #{type}
order by m.menu_sort
